function updateTimeSince(){let n=(new Date).getTime();for(let t of document.getElementsByClassName("timesince")){let u=Date.parse(t.textContent),i=(n-u)/6e4;if(i<0){t.textContent=`A short time ago`;continue}if(i<60){t.textContent=`${Math.trunc(i)} minutes ago`;continue}let r=i/60;if(r<24){t.textContent=`${Math.trunc(r)} hours ago`;continue}let f=r/24;t.textContent=`${Math.trunc(f)} days ago`}}window.onload=()=>updateTimeSince();